SELECT *
FROM catalog_data.product_category
WHERE id = #{id, jdbcType=BIGINT};


SELECT *
FROM catalog_data.product_category


        ORDER BY id DESC
        LIMIT 10;


        WHERE id IN

            #{id, jdbcType=BIGINT}

WITH RECURSIVE result AS (SELECT * FROM catalog_data.product_category WHERE id = #{id, jdbcType=BIGINT}
UNION ALL
SELECT catalog_data.product_category.* FROM catalog_data.product_category JOIN result ON catalog_data.product_category.parent_id = result.id)
SELECT * FROM result;

SELECT *
FROM catalog_data.product_category
WHERE parent_id = #{id, jdbcType=BIGINT};

WITH RECURSIVE result AS (SELECT *, 1 AS level FROM catalog_data.product_category WHERE id = #{id, jdbcType=BIGINT}
UNION ALL
SELECT catalog_data.product_category.*, result.level + 1 AS level FROM catalog_data.product_category JOIN result ON catalog_data.product_category.parent_id = result.id)
SELECT * FROM result;
